import React, { useEffect } from "react";
import { CardConfig } from "../config/CardConfig";
import { AiOutlineWhatsApp, AiOutlineInstagram, AiOutlineRollback } from "react-icons/ai";
import Logo from '../img/logo.png'
import { Container } from "react-bootstrap";
export const Convite = () => {

    const urlParams = new URLSearchParams(window.location.search);
    const Id = urlParams.get("Produto")
    const card = CardConfig().find(x => x.Id === parseInt(Id));
    return (
        <div style={{ display: 'flex' }}>
            <div style={{ width: '80vw', height: '80vh' }}>
                <div>
                    <button className="btn btn-success" onClick={() => window.history.back()}><AiOutlineRollback size={30} /></button>
                </div>
                <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                    <img style={{ width: "50%", height: '400px', padding: '10px' }} src={card?.Img[card?.NomeImg]} />
                </div>
                <div style={{ padding: '10px', borderTop: '1px solid rgb(199 196 196)' }}>
                    <div>
                        <label style={{opacity: 0.7}}>Descrição:</label>
                    </div>
                    <div>
                        {card?.Descricao}
                    </div>
                </div>
            </div>
            <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', flexDirection: 'column', border: '1px solid rgb(199 196 196)', height: '80vh' }}>
                <div>
                    <img style={{ width: '18rem', height: '100%' }} src={Logo} />
                </div>
                <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', padding: '10px' }}>
                    <button className="btn btn-primary">ENVIAR MENSAGEM<AiOutlineWhatsApp size={30} /></button>
                    <button className="btn btn-secondary">ENVIAR MENSAGEM<AiOutlineInstagram size={30} color='#fff' /></button>
                </div>
            </div>
        </div>
    );
}
