import React, { useState,useEffect } from "react";
import { Container, Row } from "react-bootstrap";

import { CardConfig } from "../config/CardConfig";

import Card from '../components/Card'

export default Index => {
    var QuebraLinha = [0, 3, 6, 9, 12, 15, 18];

    useEffect(()=>{
        GetCards();
    },[])


    function QuebrarLinha(index) {
        const found = QuebraLinha.find(element => element === index);

        if (found !== null || found !== undefined) {
            return true;
        } else {
            return false;
        }

    }

    function GetCards() {
        debugger
        var div = document.getElementById('divCard');
        var cards = CardConfig();
        window.localStorage.clear();
        for (let index = 0; index < cards.length; index++) {
            const card = document.createElement('div');
            card.className = 'card';
            card.style.width = '18rem';
            card.style.overflow = 'hidden';

            const divImg = document.createElement('div');
            divImg.style.display = 'flex';
            divImg.style.justifyContent = 'center';
            divImg.style.alignItems = 'center';

            const img = document.createElement('img');
            img.style.width = '18rem';
            img.style.height = '250px';
            img.src = cards[index].Img[cards[index].NomeImg];

            const cardBody = document.createElement('div');
            cardBody.className = 'card-body';
            const h5 = document.createElement('h5');
            h5.className = 'card-title';
            const p = document.createElement('p');
            p.className = 'card-text';
            const a = document.createElement('a');
            a.className = 'btn btn-primary';
            a.href = '#';
            a.textContent = 'Ver convite'
            divImg.append(img);
            card.append(divImg);
            cardBody.append(h5);
            cardBody.append(p);
            cardBody.append(a);
            card.append(cardBody);
            var idRow = window.localStorage.getItem('idRow');
            if (QuebrarLinha(index)) {
                if (idRow != null) {
                    var newRow = document.createElement(idRow);
                    if (newRow != null) {
                        if (newRow.children.length > 2) {
                            var newRow2 = document.createElement('div');
                            newRow2.className = 'Row';
                            newRow2.append(card);
                            var newId = 'row' + index;
                            newRow.id = newId;
                            div.append(newRow2);
                        } else {
                            newRow.className = 'Row';
                            newRow.append(card);
                            div.append(newRow);
                        }
                    }
                } else {
                    var newRow = document.createElement('div');
                    newRow.className = 'Row';
                    newRow.append(card);
                    var newId = 'row' + index;
                    newRow.id = newId;
                    div.append(newRow);
                    window.localStorage.setItem('idRow',newId);
                }
            }
        }

    }

    return (
        <div id="divCard">
        </div>
    );
}
