import React, { useEffect } from "react";
import { CardConfig } from "../config/CardConfig";
import { AiOutlineWhatsApp,AiOutlineInstagram } from "react-icons/ai";
import Logo from '../img/logo.png'
export const Convite = () => {

    const urlParams = new URLSearchParams(window.location.search);
    const Id = urlParams.get("Produto")
    const card = CardConfig().find(x => x.Id === parseInt(Id));
    return (
        <div style={{display:'flex'}}>
            <div style={{width:'80vw'}}>
                <div className="convite-img">
                    <img style={{ width: '18rem', height: '250px' }} src={card?.Img[card?.NomeImg]} />
                </div>
                <div className="convite-descricao">
                    {card?.Descricao}
                </div>
            </div>
            <div style={{display:'flex',justifyContent:'center',alignItems:'center',flexDirection:'column', border:'1px solid',height:'80vh'}}>
                <div>
                    <img style={{ width: '18rem', height: '250px' }} src={Logo} />
                </div>
                <div>
                    <button className="btn btn-primary">ENVIAR MENSAGEM<AiOutlineWhatsApp/></button>
                    <button className="btn btn-info">ENVIAR MENSAGEM<AiOutlineInstagram size={25} color='#fff'/></button>
                </div>
            </div>
        </div>
    );
}
