import React from "react";
import { Container, Row } from "react-bootstrap";

import { CardConfig } from "../config/CardConfig"; 

import Card from '../components/Card'

// {CardConfig().map(x=>{
//     console.log(x)
//     return (
//         <Row>
//             <Card Titulo={x.Titulo} Texto={x.Texto} Img={x.Img[x.NomeImg]} />
//         </Row>
//     );
// })}

export default Index => {
    var QuebraLinha = [0,3,6,9,12,15,18];

    function QuebrarLinha(index){
        const found = array1.find(element => element === index);

        if(found !== null || found !== undefined)
        {
            return true;
        }else
        {
            return false;
        }

    }

    function GetCards()
    {

        var div = document.getElementById('divCard');
        var cards = CardConfig();
        
        for (let index = 0; index < cards.length; index++) {
            if(QuebrarLinha(index))
            {
                var Row = document.createElement('div');
                Row.className = 'Row'

            }
        }

    }

    return (
        <div id="divCard"></div>
    );
}
