import React, { useState,useEffect } from "react";
import { Container, Row } from "react-bootstrap";

import { CardConfig } from "../config/CardConfig";

import Card from '../components/Card'

export default Index => {
    var QuebraLinha = [0, 3, 6, 9, 12, 15, 18];

    useEffect(()=>{
        GetCards();
    },[])


    function QuebrarLinha(index) {
        const found = QuebraLinha.find(element => element === index);

        if (found !== null || found !== undefined) {
            return found;
        } else {
            return null;
        }

    }

    function GetCards() {
        var cards = CardConfig();
        var idRow = '';
        var divCard = document.getElementById('divCard');
        for (let index = 0; index < cards.length; index++) {
            const valorLinhaQuebra = QuebrarLinha(index);
            const element = cards[index];
            if(valorLinhaQuebra != null)
            {
                if(idRow != '')
                {
                    var div = document.getElementById(idRow);
                    if(div.children.length === 3)
                    {
                        idRow = 'row'+valorLinhaQuebra;
                        const novaDiv = document.createElement('div');
                        novaDiv.id = idRow;
                        novaDiv.append(<Card Img={element.Img[element.NomeImg]} Titulo={element.Titulo} Texto={element.Texto}/>);
                        divCard.append(novaDiv);
                    }else
                    {
                        div.append(<Card Img={element.Img[element.NomeImg]} Titulo={element.Titulo} Texto={element.Texto}/>);
                        divCard.append(div);
                    }
                }else
                {
                    idRow = 'row'+valorLinhaQuebra;
                    var div = document.createElement('div');
                    div.id = idRow;
                    div.append(<Card Img={element.Img[element.NomeImg]} Titulo={element.Titulo} Texto={element.Texto}/>);
                    divCard.append(div);
                }
            }else
            {
                var div = document.getElementById(idRow);
                div.append(<Card Img={element.Img[element.NomeImg]} Titulo={element.Titulo} Texto={element.Texto}/>);
                divCard.append(div);
            }
        }
    }

    return (
        <div id="divCard">
        </div>
    );
}
