import React, { useEffect } from "react";
import { CardConfig } from "../config/CardConfig";


export const Convite = () => {

    const urlParams = new URLSearchParams(window.location.search);
    const Id = urlParams.get("Produto")
    const card = CardConfig().find(x => x.Id === parseInt(Id));
    return (
        <div>
            <div>
                <div className="convite-img">
                    <img style={{ width: '18rem', height: '250px' }} src={card?.Img[card?.NomeImg]} />
                </div>
                <div className="convite-descricao">
                    {card?.Descricao}
                </div>
            </div>
            <div className="convite-contatos">
                <div>

                </div>
                <div>
                    <button className="btn btn-secondary">ENVIAR MENSAGEM</button>
                </div>
            </div>
        </div>
    );
}
